mapper.select:

	SELECT RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, RCOUNT FROM
		(SELECT ROWNUM RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, rcount FROM
			(SELECT IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, count(rcount)  rcount FROM
				(SELECT hi.IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, ri.iid rcount
				FROM hotel_inquiry hi, reply_inquiry ri 
				where hi.iid = ri.iid(+)
				)
				GROUP BY IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID
				order by idate desc
			)      
		) 
	WHERE MID=#{MID}

select rno, iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate from (select rownum rno, iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate from (select iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate from hotel_inquiry
where title like '%'||#{keyword}||'%' order by idate desc)
where hotelname like '%'||#{keyword}||'%' order by idate desc)
where category like '%'||#{keyword}||'%' order by idate desc)
) where rno between #{start} and #{end}


	SELECT RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, RCOUNT FROM
		(SELECT ROWNUM RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, rcount FROM
			(SELECT IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, count(rcount) rcount FROM
				(SELECT hi.IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, ri.iid rcount
				FROM hotel_inquiry hi, reply_inquiry ri 
				where hi.iid = ri.iid(+)
				)
				GROUP BY IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE
				order by idate desc
			)      
		) 
	WHERE RNO BETWEEN #{start} AND #{end}
